#include <stdio.h>

int getElement(int arr[], int index) {
    return arr[index]; // O(1)
}

int main() {
    int myArray[] = {10, 20, 30, 40, 50};
    printf("Element at index 2: %d\n", getElement(myArray, 2));
    return 0;
}

